export const Message = ({ messageInfo, isCurrentUser  }) => {
	const formattedMessage = messageInfo.message.split('\n').map((line, index) => (
		<span key={index}>
			{line}
			<br />
		</span>
	));

	return (
		<div className={`w-fit ${isCurrentUser  ? 'bg-blue-100' : 'bg-gray-100'}`}>
			<span className="text-sm text-slate-600">{messageInfo.userName}</span>
			<div className={`p-2 rounded-lg shadow-md ${isCurrentUser  ? 'bg-blue-200' : 'bg-gray-100'}`}>
				{formattedMessage}
			</div>
		</div>
	);
};
